    const id = location.pathname;
    useEffect(()=>{
        axios.get(`http://localhost:5000/posts/${id}`)
         .then(result=>{
             console.log(result.data);
         })
    },[]);
    return(
        <div className="single-post-container">
            <p>single post....</p>
        </div>
    )
}

export default SinglePost;
